#!/bin/bash

# Params:
experiment_dir="/mnt/c/Users/DerFu/Documents/khronos/data/tmp"                        # Which experiment to evaluate.
evaluation_config=$(rospack find khronos_eval)/config/evaluate_mesh.yaml                         # Which evaluation config to use.
gt_mesh_file="/mnt/c/Users/DerFu/Documents/khronos/ground_truth/archviz/surface_cloud_10000.ply" # Ground truth mesh file to use.
force_recompute=false                                                                            # Force recomputation if results already exist.

# Autogenerated arguments.
exec_dir=$(catkin_find --first-only --without-underlays --libexec khronos_eval)

# Run evaluation.
eval $exec_dir/exp_evaluate_mesh $evaluation_config $experiment_dir $gt_mesh_file $force_recompute
